function initLogin(){var e=$("#loginForm");e.submit(function(){showLoader(e);e.find(".ajaxResponseContainer").hide();$.ajax({url:"/auth/login",type:"post",data:$(this).serialize(),dataType:"json",success:function(t){if(t.status){window.location.replace("/members-area/view-scores")}else{hideLoader(e);var n="";for(var r in t.errors){n+="<div>"+t.errors[r]+"</div>"}e.find(".ajaxResponseContainer").addClass("errors").html(n).show()}}});return false})}function initPwReset(){var e=$("#pwResetForm");e.submit(function(){showLoader(e);e.find(".ajaxResponseContainer").hide();$.ajax({url:"/index/password-reset-email",type:"post",data:$(this).serialize(),dataType:"json",success:function(t){hideLoader(e);if(t.length){var n="";for(var r in t){n+="<div>"+t[r]+"</div>"}e.find(".ajaxResponseContainer").addClass("errors").html(n).show()}else{e.find(".ajaxResponseContainer").addClass("success").html("<div>Email sent. Thank you.</div>").show()}}});return false})}$(document).ready(function(){initRssFeed("http://www.thegolfchannel.com/rss/?FeedID=NewsArchive","golf-channel-rss");initLogin();initPwReset();$("#fgt-pswd-modal").dialog({title:"Password Reset",autoOpen:false,modal:true,width:500});$("#fgt-pswd").click(function(){$("#fgt-pswd-modal").dialog("open")})});