function initAccountUpdate(){var e=$("#memberDataForm");e.submit(function(){showLoader(e);e.find(".ajaxResponseContainer").hide();$.ajax({url:"/members-area/save-member-data",type:"post",data:$(this).serialize(),dataType:"json",success:function(t){hideLoader(e);if(!t.length){notify("Your account has been updated.","Account Updated");$("#pw1, #pw2").val("")}else{hideLoader(e);var n="";for(var r in t){n+="<div>"+t[r]+"</div>"}e.find(".ajaxResponseContainer").addClass("errors").html(n).show()}}});return false})}$(document).ready(function(){initAccountUpdate();$(".deleteAcctLinkContainer").click(function(){if(confirm("Are you sure you want to delete your account? All of your scores and account data will be permanently erased!")){$.ajax({url:"/members-area/delete-account",success:function(){window.location.href="/index"}})}});$("#pw1, #pw2").val("");$(".changePwLinkContainer").click(function(){var e=$(".changePwContainer");if(e.is(":visible")){e.slideUp();$("#pw1, #pw2").val("")}else{e.slideDown()}});refreshStats();$.get("/members-area/get-monthly-graph-data",function(e){if(e.length>0){e=e[0];var t=[];var n=0;var r="";for(var i in e){var s=[i,e[i]];t.push(s);if(n===0){r=i}n++}$.jqplot("hcp-graph",[t],{title:"Handicap Index History (past year)",axes:{xaxis:{renderer:$.jqplot.DateAxisRenderer,tickOptions:{formatString:"%b %Y"},min:r,tickInterval:"1 month",pad:1.5},yaxis:{tickOptions:{formatString:"%.1f"},pad:1.5}},highlighter:{showTooltip:true},series:[{pointLabels:{show:true}}]})}},"json")})