function initSaveNewPassword(){var e=$("#resetPwForm");e.submit(function(){showLoader(e);e.find(".ajaxResponseContainer").hide();$.ajax({url:"/index/save-new-password",type:"post",data:$(this).serialize(),dataType:"json",success:function(t){hideLoader(e);if(t.length){var n="";for(var r in t){n+="<div>"+t[r]+"</div>"}e.find(".ajaxResponseContainer").addClass("errors").html(n).show()}else{e.find(".ajaxResponseContainer").addClass("success").html('<div>Your password has been reset. Please click <a href="/index">here</a> to login.</div>').show()}}});return false})}$(document).ready(function(){initSaveNewPassword()});