function initRegister(){var e=$("#registerForm");e.submit(function(){showLoader(e);$(".ajaxResponseContainer").hide();$.ajax({url:"do-register",type:"post",data:$(this).serialize(),dataType:"json",success:function(t){if(t.status){window.location.href="members-area/view-scores"}else{hideLoader(e);var n="";for(var r in t.errors){n+="<div>"+t.errors[r]+"</div>"}$(".ajaxResponseContainer").addClass("errors").html(n).show()}}});return false})}$(document).ready(function(){initRegister();initRssFeed("http://www.thegolfchannel.com/rss/?FeedID=NewsArchive","golf-channel-rss");$("#tandc-modal").dialog({autoOpen:false,modal:true,width:400});$("#tancd-link").click(function(){$("#tandc-modal").dialog("open")})})